sql.if:
where 1 = 1

	and lower(FLM.NUME) like lower(#{qry.filter.nume})


	and ACF.ID_ACTOR = #{qry.filter.actor.id}


	and FAV.ID_USER = #{qry.filter.user.id}

mapper.update:
		update FILM
     	set 
     		  NUME = #{record.nume}
			, GENURI = #{record.genuri}
			, DT = #{record.dt}
			, DESCRIERE = #{record.descriere}
		where
			ID = #{record.id}

mapper.insert:

	select SEQ_FILM.NEXTVAL from DUAL

insert into FILM(
	  ID
	, NUME
	, GENURI
	, DT
	, ID_DOCUMENT
	, DESCRIERE
) values(
	  #{record.id}
	, #{record.nume}
	, #{record.genuri}
	, #{record.dt}
	, #{record.document.id}
	, #{record.descriere}
)
